import React from 'react';
import './BenefitsSection.css';
import pic1 from './pic1.png';
import pic2 from './pic2.png';
import pic3 from './pic3.png';

function BenefitsSection() {
  const benefits = [
    {
      image: pic1,
      title: "Manual monitoring of AC systems is costing you time, energy, and money",
      description: "Traditional AC monitoring methods are inefficient and prone to human error, leading to increased costs and energy waste.",
    },
    {
      image: pic2,
      title: "Smarter energy management for smarter governance—let us help you achieve it",
      description: "Our intelligent HVAC solutions provide real-time monitoring and optimization, ensuring maximum efficiency and comfort.",
    },
    {
      image: pic3,
      title: "Inefficient HVAC systems could be draining your resources without you knowing!",
      description: "Inefficient HVAC outcomes can significantly increase your utility bills and carbon footprints. Our solutions help you avoid these issues.",
    },
  ];

  return (
    <div className="benefits-section">
      {benefits.map((benefit, index) => (
        <div className={`benefit-item ${index % 2 !== 0 ? 'row-reverse' : ''}`} key={index}>
          <img src={benefit.image} alt={benefit.title} className="benefit-image" />
          <div className="benefit-text">
            <h3>{benefit.title}</h3>
            <p>{benefit.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default BenefitsSection;
